import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import apiService from '../../services/apiService';
import './AdminPanel.css';

const AdminPanel = () => {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const fetchUsers = async () => {
    setLoading(true);
    setError('');
    
    try {
      const response = await apiService.getAllUsers();
      if (response.success) {
        setUsers(response.users);
      } else {
        setError(response.error);
      }
    } catch (error) {
      setError('Có lỗi xảy ra khi tải danh sách người dùng');
    } finally {
      setLoading(false);
    }
  };

  const handleRoleChange = async (userId, newRole) => {
    setLoading(true);
    setError('');
    setSuccess('');

    const permissions = newRole === 'admin' 
      ? ['read', 'write', 'delete', 'manage_users']
      : ['read'];

    try {
      const response = await apiService.updateUserRole(userId, newRole, permissions);
      if (response.success) {
        setSuccess(`Đã cập nhật role thành công!`);
        fetchUsers(); // Refresh list
      } else {
        setError(response.error);
      }
    } catch (error) {
      setError('Có lỗi xảy ra khi cập nhật role');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteUser = async (userId, userName) => {
    if (!window.confirm(`Bạn có chắc muốn xóa user "${userName}"?`)) {
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const response = await apiService.deleteUser(userId);
      if (response.success) {
        setSuccess(`Đã xóa user "${userName}" thành công!`);
        fetchUsers(); // Refresh list
      } else {
        setError(response.error);
      }
    } catch (error) {
      setError('Có lỗi xảy ra khi xóa user');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (user && user.role === 'admin') {
      fetchUsers();
    }
  }, [user]);

  // Check if user is admin
  if (!user || user.role !== 'admin') {
    return (
      <div className="admin-panel">
        <div className="access-denied">
          <h2>🚫 Access Denied</h2>
          <p>Bạn không có quyền truy cập trang quản trị.</p>
          <p>Chỉ admin mới có thể truy cập trang này.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-panel">
      <div className="admin-header">
        <h1>🛠️ Admin Panel</h1>
        <p>Quản lý người dùng và phân quyền</p>
      </div>

      {error && (
        <div className="alert error">
          <i className="fas fa-exclamation-triangle"></i>
          {error}
        </div>
      )}

      {success && (
        <div className="alert success">
          <i className="fas fa-check-circle"></i>
          {success}
        </div>
      )}

      <div className="admin-stats">
        <div className="stat-card">
          <h3>Tổng Users</h3>
          <div className="stat-number">{users.length}</div>
        </div>
        <div className="stat-card">
          <h3>Admins</h3>
          <div className="stat-number">{users.filter(u => u.role === 'admin').length}</div>
        </div>
        <div className="stat-card">
          <h3>Regular Users</h3>
          <div className="stat-number">{users.filter(u => u.role === 'user').length}</div>
        </div>
      </div>

      <div className="users-management">
        <div className="section-header">
          <h2>Quản lý người dùng</h2>
          <button onClick={fetchUsers} className="refresh-btn" disabled={loading}>
            <i className="fas fa-sync-alt"></i>
            Làm mới
          </button>
        </div>

        {loading && (
          <div className="loading-indicator">
            <i className="fas fa-spinner fa-spin"></i>
            Đang tải...
          </div>
        )}

        <div className="users-table">
          <table>
            <thead>
              <tr>
                <th>User</th>
                <th>Email</th>
                <th>Role</th>
                <th>Permissions</th>
                <th>Ngày tạo</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(u => (
                <tr key={u.id}>
                  <td>
                    <div className="user-info">
                      <img src={u.avatar} alt={u.name} className="user-avatar" />
                      <div>
                        <div className="user-name">{u.name}</div>
                        <div className="user-id">ID: {u.id}</div>
                      </div>
                    </div>
                  </td>
                  <td>{u.email}</td>
                  <td>
                    <span className={`role-badge ${u.role}`}>{u.role}</span>
                  </td>
                  <td>
                    <div className="permissions">
                      {u.permissions.map(perm => (
                        <span key={perm} className="permission-tag">{perm}</span>
                      ))}
                    </div>
                  </td>
                  <td>
                    {u.createdAt ? new Date(u.createdAt).toLocaleDateString('vi-VN') : 'N/A'}
                  </td>
                  <td>
                    <div className="action-buttons">
                      {u.id !== user.id && (
                        <>
                          <select 
                            value={u.role} 
                            onChange={(e) => handleRoleChange(u.id, e.target.value)}
                            disabled={loading}
                            className="role-selector"
                          >
                            <option value="user">User</option>
                            <option value="admin">Admin</option>
                          </select>
                          <button 
                            onClick={() => handleDeleteUser(u.id, u.name)}
                            className="delete-btn"
                            disabled={loading}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </>
                      )}
                      {u.id === user.id && (
                        <span className="current-user">You</span>
                      )}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <div className="role-info">
        <h3>Thông tin về Roles & Permissions</h3>
        <div className="role-cards">
          <div className="role-card">
            <h4>👤 User</h4>
            <ul>
              <li>✅ Xem sản phẩm</li>
              <li>✅ Thêm vào giỏ hàng</li>
              <li>✅ Xem profile cá nhân</li>
              <li>❌ Không thể quản lý users khác</li>
            </ul>
            <div className="permissions-list">
              <strong>Permissions:</strong> read
            </div>
          </div>
          <div className="role-card">
            <h4>👨‍💼 Admin</h4>
            <ul>
              <li>✅ Tất cả quyền của User</li>
              <li>✅ Quản lý danh sách users</li>
              <li>✅ Thay đổi role của users</li>
              <li>✅ Xóa users</li>
            </ul>
            <div className="permissions-list">
              <strong>Permissions:</strong> read, write, delete, manage_users
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
