import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { products } from '../../data/products';
import { useCart } from '../../context/CartContext';
import ProductCard from '../../components/ProductCard/ProductCard';
import './ProductDetail.css';

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);

  const product = products.find(p => p.id === parseInt(id));

  if (!product) {
    return (
      <div className="product-not-found">
        <div className="container">
          <h2>Sản phẩm không tồn tại</h2>
          <button onClick={() => navigate('/products')} className="btn btn-primary">
            Về trang sản phẩm
          </button>
        </div>
      </div>
    );
  }

  // Mock additional images
  const productImages = [
    product.image,
    product.image.replace('w=400', 'w=400&sat=-20'),
    product.image.replace('w=400', 'w=400&hue=30'),
    product.image.replace('w=400', 'w=400&sepia=50')
  ];

  const relatedProducts = products
    .filter(p => p.id !== product.id && (p.category === product.category || p.brand === product.brand))
    .slice(0, 4);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND'
    }).format(price * 1000);
  };

  const getDiscountPercentage = () => {
    if (product.originalPrice && product.originalPrice > product.price) {
      return Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100);
    }
    return 0;
  };

  const handleAddToCart = () => {
    for (let i = 0; i < quantity; i++) {
      addToCart(product);
    }
    // Show notification or redirect to cart
  };

  const handleBuyNow = () => {
    handleAddToCart();
    navigate('/cart');
  };

  return (
    <div className="product-detail">
      <div className="container">
        {/* Breadcrumb */}
        <nav className="breadcrumb">
          <button onClick={() => navigate('/')}>Trang chủ</button>
          <span>/</span>
          <button onClick={() => navigate('/products')}>Sản phẩm</button>
          <span>/</span>
          <span>{product.name}</span>
        </nav>

        <div className="product-detail-content">
          {/* Product Images */}
          <div className="product-images">
            <div className="main-image">
              <img 
                src={productImages[selectedImage]} 
                alt={product.name}
                className="main-product-image"
              />
              {!product.inStock && (
                <div className="out-of-stock-overlay">
                  <span>Hết hàng</span>
                </div>
              )}
              {getDiscountPercentage() > 0 && (
                <div className="discount-badge">-{getDiscountPercentage()}%</div>
              )}
            </div>
            
            <div className="image-thumbnails">
              {productImages.map((image, index) => (
                <button
                  key={index}
                  className={`thumbnail ${selectedImage === index ? 'active' : ''}`}
                  onClick={() => setSelectedImage(index)}
                >
                  <img src={image} alt={`${product.name} ${index + 1}`} />
                </button>
              ))}
            </div>
          </div>

          {/* Product Info */}
          <div className="product-info">
            <div className="product-header">
              <span className="product-brand">{product.brand}</span>
              <h1 className="product-title">{product.name}</h1>
              
              <div className="product-rating">
                <div className="stars">
                  {[...Array(5)].map((_, i) => (
                    <i 
                      key={i} 
                      className={`fas fa-star ${i < Math.floor(product.rating) ? 'filled' : ''}`}
                    ></i>
                  ))}
                </div>
                <span className="rating-text">
                  {product.rating} ({product.reviews} đánh giá)
                </span>
              </div>
            </div>

            <div className="product-pricing">
              <div className="price-container">
                <span className="current-price">{formatPrice(product.price)}</span>
                {product.originalPrice && product.originalPrice > product.price && (
                  <span className="original-price">{formatPrice(product.originalPrice)}</span>
                )}
              </div>
              {getDiscountPercentage() > 0 && (
                <div className="savings">
                  Tiết kiệm {formatPrice(product.originalPrice - product.price)}
                </div>
              )}
            </div>

            <div className="product-description">
              <p>{product.description}</p>
            </div>

            {/* Features */}
            <div className="product-features">
              <h3>Tính năng nổi bật</h3>
              <ul>
                {product.features.map((feature, index) => (
                  <li key={index}>
                    <i className="fas fa-check"></i>
                    {feature}
                  </li>
                ))}
              </ul>
            </div>

            {/* Add to Cart */}
            <div className="product-actions">
              <div className="quantity-selector">
                <label>Số lượng:</label>
                <div className="quantity-controls">
                  <button 
                    onClick={() => setQuantity(Math.max(1, quantity - 1))}
                    disabled={!product.inStock}
                  >
                    -
                  </button>
                  <span className="quantity">{quantity}</span>
                  <button 
                    onClick={() => setQuantity(quantity + 1)}
                    disabled={!product.inStock}
                  >
                    +
                  </button>
                </div>
              </div>

              <div className="action-buttons">
                <button 
                  className="btn btn-primary add-to-cart"
                  onClick={handleAddToCart}
                  disabled={!product.inStock}
                >
                  <i className="fas fa-shopping-cart"></i>
                  Thêm vào giỏ hàng
                </button>
                
                <button 
                  className="btn btn-secondary buy-now"
                  onClick={handleBuyNow}
                  disabled={!product.inStock}
                >
                  Mua ngay
                </button>
              </div>
            </div>

            {/* Stock Status */}
            <div className={`stock-status ${product.inStock ? 'in-stock' : 'out-of-stock'}`}>
              <i className={`fas ${product.inStock ? 'fa-check-circle' : 'fa-times-circle'}`}></i>
              {product.inStock ? 'Còn hàng' : 'Hết hàng'}
            </div>
          </div>
        </div>

        {/* Product Details Tabs */}
        <div className="product-tabs">
          <div className="tab-headers">
            <button className="tab-header active">Mô tả</button>
            <button className="tab-header">Thông số kỹ thuật</button>
            <button className="tab-header">Đánh giá</button>
          </div>
          
          <div className="tab-content">
            <div className="tab-panel active">
              <h3>Mô tả sản phẩm</h3>
              <p>{product.description}</p>
              <p>
                Đồng hồ {product.name} từ thương hiệu {product.brand} là sự kết hợp hoàn hảo 
                giữa thiết kế tinh tế và công nghệ hiện đại. Với chất lượng vượt trội và 
                độ bền cao, đây là lựa chọn hoàn hảo cho những ai yêu thích phong cách 
                {product.category === 'luxury' ? ' sang trọng và đẳng cấp' : 
                 product.category === 'sport' ? ' năng động và thể thao' : ' hiện đại và thời trang'}.
              </p>
            </div>
          </div>
        </div>

        {/* Related Products */}
        {relatedProducts.length > 0 && (
          <div className="related-products">
            <h2>Sản phẩm liên quan</h2>
            <div className="related-products-grid">
              {relatedProducts.map(product => (
                <ProductCard key={product.id} product={product} />
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductDetail;
