import React, { useState, useEffect } from 'react';
import apiService from '../../services/apiService';
import './BackendStatus.css';

const BackendStatus = () => {
  const [status, setStatus] = useState('checking'); // checking, connected, error
  const [message, setMessage] = useState('Đang kiểm tra kết nối...');

  useEffect(() => {
    const checkBackendConnection = async () => {
      try {
        const response = await apiService.healthCheck();
        if (response.success) {
          setStatus('connected');
          setMessage('Kết nối backend thành công');
        } else {
          setStatus('error');
          setMessage('Không thể kết nối với backend');
        }
      } catch (error) {
        setStatus('error');
        setMessage('Backend không khả dụng');
      }
    };

    checkBackendConnection();
    
    // Kiểm tra lại mỗi 30 giây
    const interval = setInterval(checkBackendConnection, 30000);
    
    return () => clearInterval(interval);
  }, []);

  if (status === 'checking') {
    return (
      <div className="backend-status checking">
        <i className="fas fa-spinner fa-spin"></i>
        <span>{message}</span>
      </div>
    );
  }

  if (status === 'connected') {
    return (
      <div className="backend-status connected">
        <i className="fas fa-check-circle"></i>
        <span>{message}</span>
      </div>
    );
  }

  return (
    <div className="backend-status error">
      <i className="fas fa-exclamation-triangle"></i>
      <span>{message}</span>
    </div>
  );
};

export default BackendStatus;
