const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

class APIService {
  constructor() {
    this.baseURL = API_BASE_URL;
  }

  // Helper method để tạo request options
  createRequestOptions(method = 'GET', data = null, includeAuth = true) {
    const options = {
      method,
      headers: {
        'Content-Type': 'application/json',
      },
    };

    // Thêm token vào header nếu cần
    if (includeAuth) {
      const token = localStorage.getItem('token');
      if (token) {
        options.headers.Authorization = `Bearer ${token}`;
      }
    }

    // Thêm body data nếu có
    if (data && (method === 'POST' || method === 'PUT' || method === 'PATCH')) {
      options.body = JSON.stringify(data);
    }

    return options;
  }

  // Helper method để xử lý response
  async handleResponse(response) {
    const data = await response.json();
    
    if (!response.ok) {
      throw new Error(data.error || `HTTP error! status: ${response.status}`);
    }
    
    return data;
  }

  // Auth methods
  async register(userData) {
    try {
      const response = await fetch(`${this.baseURL}/auth/register`, 
        this.createRequestOptions('POST', userData, false)
      );
      
      const result = await this.handleResponse(response);
      
      // Lưu token vào localStorage
      if (result.success && result.token) {
        localStorage.setItem('token', result.token);
      }
      
      return result;
    } catch (error) {
      console.error('Register API error:', error);
      return {
        success: false,
        error: error.message || 'Có lỗi xảy ra khi đăng ký'
      };
    }
  }

  async login(email, password) {
    try {
      const response = await fetch(`${this.baseURL}/auth/login`, 
        this.createRequestOptions('POST', { email, password }, false)
      );
      
      const result = await this.handleResponse(response);
      
      // Lưu token vào localStorage
      if (result.success && result.token) {
        localStorage.setItem('token', result.token);
      }
      
      return result;
    } catch (error) {
      console.error('Login API error:', error);
      return {
        success: false,
        error: error.message || 'Có lỗi xảy ra khi đăng nhập'
      };
    }
  }

  async getCurrentUser() {
    try {
      const response = await fetch(`${this.baseURL}/auth/me`, 
        this.createRequestOptions('GET', null, true)
      );
      
      return await this.handleResponse(response);
    } catch (error) {
      console.error('Get current user API error:', error);
      return {
        success: false,
        error: error.message || 'Có lỗi xảy ra khi lấy thông tin người dùng'
      };
    }
  }

  async getAllUsers() {
    try {
      const response = await fetch(`${this.baseURL}/users`, 
        this.createRequestOptions('GET', null, true)
      );
      
      return await this.handleResponse(response);
    } catch (error) {
      console.error('Get users API error:', error);
      return {
        success: false,
        error: error.message || 'Có lỗi xảy ra khi lấy danh sách người dùng'
      };
    }
  }

  // Health check
  async healthCheck() {
    try {
      const response = await fetch(`${this.baseURL}/health`, 
        this.createRequestOptions('GET', null, false)
      );
      
      return await this.handleResponse(response);
    } catch (error) {
      console.error('Health check API error:', error);
      return {
        success: false,
        error: error.message || 'Không thể kết nối với server'
      };
    }
  }

  // Logout (clear token)
  logout() {
    localStorage.removeItem('token');
    return { success: true };
  }
}

// Export singleton instance
const apiService = new APIService();
export default apiService;
