import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import apiService from '../../services/apiService';
import APITester from '../APITester/APITester';
import './UserProfile.css';

const UserProfile = () => {
  const { user, isAuthenticated } = useAuth();
  const [userDetails, setUserDetails] = useState(null);
  const [allUsers, setAllUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Lấy thông tin user hiện tại từ backend
  const fetchCurrentUser = async () => {
    setLoading(true);
    setError('');
    
    try {
      const response = await apiService.getCurrentUser();
      if (response.success) {
        setUserDetails(response.user);
      } else {
        setError(response.error || 'Không thể lấy thông tin người dùng');
      }
    } catch (error) {
      setError('Có lỗi xảy ra khi lấy thông tin người dùng');
    } finally {
      setLoading(false);
    }
  };

  // Lấy danh sách tất cả users (demo - chỉ cho admin)
  const fetchAllUsers = async () => {
    setLoading(true);
    setError('');
    
    try {
      const response = await apiService.getAllUsers();
      if (response.success) {
        setAllUsers(response.users);
      } else {
        setError(response.error || 'Không thể lấy danh sách người dùng');
      }
    } catch (error) {
      setError('Có lỗi xảy ra khi lấy danh sách người dùng');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (isAuthenticated) {
      fetchCurrentUser();
    }
  }, [isAuthenticated]);

  if (!isAuthenticated) {
    return (
      <div className="user-profile-container">
        <div className="not-authenticated">
          <h2>Bạn chưa đăng nhập</h2>
          <p>Vui lòng đăng nhập để xem thông tin profile</p>
        </div>
      </div>
    );
  }

  return (
    <div className="user-profile-container">
      <div className="user-profile-content">
        <h1>Thông tin tài khoản</h1>

        {error && (
          <div className="error-message">
            <i className="fas fa-exclamation-triangle"></i>
            {error}
          </div>
        )}

        {/* Thông tin user hiện tại */}
        <div className="profile-section">
          <h2>Thông tin cá nhân</h2>
          <div className="profile-card">
            {loading ? (
              <div className="loading">
                <i className="fas fa-spinner fa-spin"></i>
                Đang tải...
              </div>
            ) : userDetails ? (
              <div className="user-info">
                <div className="user-avatar">
                  <img src={userDetails.avatar} alt={userDetails.name} />
                </div>
                <div className="user-details">
                  <h3>{userDetails.name}</h3>
                  <p className="user-email">{userDetails.email}</p>
                  <p className="user-id">ID: {userDetails.id}</p>
                </div>
              </div>
            ) : user ? (
              <div className="user-info">
                <div className="user-avatar">
                  <img src={user.avatar} alt={user.name} />
                </div>
                <div className="user-details">
                  <h3>{user.name}</h3>
                  <p className="user-email">{user.email}</p>
                  <p className="user-id">ID: {user.id}</p>
                </div>
              </div>
            ) : (
              <p>Không có thông tin người dùng</p>
            )}
          </div>
          
          <button 
            onClick={fetchCurrentUser}
            className="refresh-btn"
            disabled={loading}
          >
            <i className="fas fa-sync-alt"></i>
            Làm mới thông tin
          </button>
        </div>

        {/* Danh sách tất cả users (demo) */}
        <div className="profile-section">
          <h2>Danh sách người dùng (Demo)</h2>
          <p className="section-description">
            Chức năng này để demo việc lấy dữ liệu từ backend API
          </p>
          
          <button 
            onClick={fetchAllUsers}
            className="load-users-btn"
            disabled={loading}
          >
            <i className="fas fa-users"></i>
            Tải danh sách người dùng
          </button>

          {allUsers.length > 0 && (
            <div className="users-list">
              <h3>Tổng cộng: {allUsers.length} người dùng</h3>
              <div className="users-grid">
                {allUsers.map(u => (
                  <div key={u.id} className="user-card">
                    <img src={u.avatar} alt={u.name} />
                    <div className="user-card-info">
                      <h4>{u.name}</h4>
                      <p>{u.email}</p>
                      <small>ID: {u.id}</small>
                      {u.createdAt && (
                        <small>Tham gia: {new Date(u.createdAt).toLocaleDateString('vi-VN')}</small>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* API Information */}
        <div className="profile-section">
          <h2>Thông tin API</h2>
          <div className="api-info">
            <div className="api-endpoint">
              <strong>Current User:</strong> 
              <code>GET /api/auth/me</code>
            </div>
            <div className="api-endpoint">
              <strong>All Users:</strong> 
              <code>GET /api/users</code>
            </div>
            <div className="api-endpoint">
              <strong>Health Check:</strong> 
              <code>GET /api/health</code>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
